from math import sqrt, ceil

def calculate_cheese_arrangement(n, cheeses):
    x = cheeses.count('soft')
    y = cheeses.count('hard')
    if (abs(x - y) == 1 or x == y) or int(sqrt(n) + 0.5) ** 2 == n:
        return int(sqrt(n))
    else:
        ma = max(x, y)
        t = ma * 2
        if t - 1 < n:
            return ceil(sqrt(t))
        else:
            return min(ceil(sqrt(t)), ceil(sqrt(t - 1)))

