def determine_operations(N, X):
    if N == 2 or X == 2:
        return -1
    if N == 2 and X == 3:
        return [(1, 1, 2)]
    nb = bin(N)[2:]
    xb = bin(X)[2:]
    if len(xb) > len(nb):
        return -1
    if len(nb) > len(xb):
        if nb[1:] == '0' * (len(nb) - 1):
            return -1
    st_pow = set()
    i = 1
    while i <= N:
        st_pow.add(i)
        i *= 2
    operations = []
    curr = 3
    for i in range(4, N + 1):
        if i not in st_pow:
            operations.append((1, curr, i))
            curr |= i
    xb = xb[::-1]
    for i in range(len(xb)):
        p = pow(2, i)
        if xb[i] == '0':
            operations.append((2, curr, p))
            st_pow.remove(p)
            curr ^= p
        else:
            operations.append((1, curr, p))
            st_pow.remove(p)
            curr |= p
    if len(st_pow) > 0:
        a = list(st_pow)
        for z in a:
            operations.append((2, curr, z))
            curr ^= z
    return operations

