def is_finite_fraction(p: int, q: int, b: int) -> str:
    """"""
    if p == 0:
        return 'Finite'
    gcd_pq = gcd(p, q)
    q //= gcd_pq
    while q != 1:
        gcd_qb = gcd(q, b)
        if gcd_qb == 1:
            return 'Infinite'
        q //= gcd_qb
    return 'Finite'

def gcd(a: int, b: int) -> int:
    while b:
        (a, b) = (b, a % b)
    return a
