def count_lucky_substrings(k, b, n, digits):

    def ans0():
        j = -1
        answer = 0
        for i in range(n):
            if digits[i] != 0 or i < j:
                continue
            j = i
            while j < n and digits[j] == 0:
                j += 1
            r = j - i
            answer += r * (r + 1) // 2
        return answer
    if b != 0:
        return ans0()
    else:
        count = dict()
        count[0] = 1
        pref_sum = 0
        answer = 0
        if b == k - 1:
            b = 0
            answer -= ans0()
        for d in digits:
            pref_sum = (pref_sum + d) % (k - 1)
            need = (pref_sum - b) % (k - 1)
            answer += count.get(need, 0)
            count[pref_sum] = count.get(pref_sum, 0) + 1
        return answer

