def make_sequence_beautiful(test_cases):
    results = []
    for (t, num) in test_cases:
        if sum(num) % 4 != 0:
            num = [j % 4 for j in num if j % 4 != 0]
            a1 = num.count(1)
            a2 = num.count(2)
            a3 = num.count(3)
            if a1 == a3:
                ans = a1 + a2 // 2
            elif a1 < a3:
                c3 = a3 - a1
                if c3 % 4 == 0:
                    a = c3 // 4 * 3
                elif c3 % 4 == 2:
                    a = c3 // 4 * 3 + 2
                ans = a1 + a + a2 // 2
            elif a3 < a1:
                c1 = a1 - a3
                if c1 % 4 == 0:
                    a = c1 // 4 * 3
                elif c1 % 4 == 2:
                    a = c1 // 4 * 3 + 2
                ans = a3 + a + a2 // 2
            results.append(ans)
        else:
            results.append(-1)
    return results

