        (sli, mem, sma, luc) = family
        total = sma
        t = sma
        while mem > 1:
            t *= luc
            total += t
            mem -= 1
        if total <= sli:
            extra += sli - total
            results.append(f'POSSIBLE {sli - total}')
        else:
            less += total - sli
            results.append(f'IMPOSSIBLE {total * sli}')
    if extra >= less:
        final_result = 'POSSIBLE'
    else:
        final_result = 'IMPOSSIBLE'
    return (results, final_result)
