import math

def min_operations_to_make_all_ones(n, a):
    x = [1 for x in a if x == 1]
    x_count = len(x)
    if x_count > 1:
        return n - x_count
    ans = n
    for i in range(n):
        g = a[i]
        for j in range(i, n):
            g = math.gcd(g, a[j])
            if g == 1:
                ans = min(ans, j - i)
                break
    if ans == n:
        return -1
    return ans + n - 1

