def least_prime_factors(n: int) -> list[int]:
    """"""
    lpf = [i for i in range(n + 1)]
    lpf[1] = 1
    for i in range(2, n + 1):
        if i == lpf[i]:
            j = i
            while j < n + 1:
                if lpf[j] == j:
                    lpf[j] = i
                j += i
    return lpf[1:]
