def calculate_monkiness(A, B):

    def binsearch(B, item):
        if B[-1] == item:
            return len(B) - 1
        (left, right) = (0, len(B) - 1)
        while right - left > 1:
            mid = left + (right - left) // 2
            if B[mid] >= item:
                left = mid
            else:
                right = mid
        return left
    n = len(A)
    (minA, maxA, minB, maxB) = (min(A), max(A), min(B), max(B))
    if maxB <= minA:
        return 0
    if minB >= maxA:
        return n - 1
    mx = -1
    for i in range(n):
        j = binsearch(B, A[i])
        if j - i > mx:
            mx = j - i
    return mx

