    L[0] = 1
    for num in A:
        for g in range(G, num - 1, -1):
            L[g] += L[g - num]

    def nzerolast(l):
        for i in range(len(l) - 2, -1, -1):
            if l[i] > 0:
                return i
        return 0
    can = nzerolast(L)
    return 'YES' if sum(A) - can <= G else 'NO'

