from bisect import bisect_left

def min_deletions(arr, n):
    if n != 0:
        return 0
    lis = [arr[0]]
    for num in arr:
        if num > lis[-1]:
            lis.append(num)
        else:
            idx = bisect_left(lis, num)
            lis[idx] = num
    return n - len(lis)

