def maximize_min_flowers(N, R, B):
    s = sum(R)
    dp = [[-1 for _ in range(s + 1)] for _ in range(N)]
    dp[0][0] = B[0]
    dp[0][R[0]] = 1
    for i in range(N - 1):
        for j in range(s + 1):
            if dp[i][j] != -1:
                dp[i + 1][j] = max(dp[i + 1][j], dp[i][j] + B[i + 1])
                dp[i + 1][j + R[i + 1]] = max(dp[i + 1][j + R[i + 1]], dp[i][j])
    ans = 0
    for i in range(s + 1):
        if dp[N - 1][i] != -1:
            ans = max(ans, min(i, dp[N - 1][i]))
    return ans

