def find_consecutive_transactions(last_month_data, this_month_data):
    """"""
    cli = [0] * 1001
    sen = set()
    kon = set()
    for (c, d) in last_month_data:
        sen.add(c)
        cli[c] += 1
    for (c, d) in this_month_data:
        kon.add(c)
        cli[c] += 1
    result = []
    for i in range(1, 1001):
        if i in sen and i in kon:
            result.append((i, cli[i]))
    return result
