class Node:

    def __init__(self, data):
        self.data = data
        self.right = None
        self.down = None

def construct_linked_matrix(mat, n):

    def solve(row, col, mat):
        if row >= n and col >= n:
            return None
        cur = Node(mat[row][col])
        cur.right = solve(row, col + 1, mat)
        cur.down = solve(row + 1, col, mat)
        return cur
    return solve(0, 0, mat)

