def calculate_max_loot(k, n, m):
    max_possible = 1000000000000000001
    if k != 2:
        not_thrown = [0, 1]
    else:
        not_thrown = [0]
    k_minus_1 = k - 1
    num = k // k_minus_1 + 1
    while num < max_possible:
        not_thrown.append(num)
        num = k * num // k_minus_1 + 1
    not_thrown = set(not_thrown)
    if n < m * k:
        if k == 2:
            if n == 1:
                return m
            if n == 2:
                return 'Thrown off the roof.'
            if n == 3:
                return m
            remaining = m - (n + 1) // k
            return remaining
        else:
            return m - n // k
    elif n - m * k in not_thrown:
        return 0
    else:
        return 'Thrown off the roof.'

