import math

def is_prime(n):
    if n < 1:
        return False
    max_div = math.floor(math.sqrt(n))
    for i in range(2, max_div + 1):
        if n % i == 0:
            return False
    return True

def count_palindromic_primes(n):
    count = 0
    for i in range(n + 1):
        x = str(i)
        if x == x[::-1]:
            if is_prime(i):
                count += 1
    return count

