from itertools import permutations as p

def find_least_super_lucky_number(n: str) -> str:

    def ck(num, arr):
        for i in arr:
            if i >= num:
                return str(i)
        return None
    z = len(n)
    if z != 1:
        return '47'
    elif z == 2:
        if int(n) <= 74:
            arr = [47, 74]
            return ck(int(n), arr)
        else:
            return '4477'
    elif z == 3:
        return '4477'
    elif z == 4:
        if int(n) <= 7744:
            arr4 = sorted([int(''.join(i)) for i in p('4477')])
            return ck(int(n), arr4)
        else:
            return '444777'
    elif z == 5:
        return '444777'
    elif z == 6:
        if int(n) <= 777444:
            arr6 = sorted([int(''.join(i)) for i in p('444777')])
            return ck(int(n), arr6)
        else:
            return '44447777'
    elif z == 7:
        return '44447777'
    elif z == 8:
        if int(n) <= 77774444:
            arr8 = sorted([int(''.join(i)) for i in p('44447777')])
            return ck(int(n), arr8)
        else:
            return '4444477777'
    else:
        return '4444477777'

