import numpy as np

def max_substring_difference(S: str) -> int:
    """"""
    n = len(S)
    dp = np.array([0] * (n + 1))
    for j in range(n):
        if S[j] == '1':
            dp[j + 1] = max(dp[j] - 1, -1)
        else:
            dp[j + 1] = max(dp[j] + 1, 1)
    return np.max(dp) if '0' in S else -1
