def construct_running_track(s: str, t: str) -> int or list:
    p = []
    d = [[] for _ in range(26)]
    for (i, char) in enumerate(s):
        d[ord(char) - 97].append(i)
    s -= '+'
    t += '-'
    (i, n) = (0, len(t) - 1)
    while i < n:
        q = t[i]
        a = b = c = 0
        for j in d[ord(q) - 97]:
            k = 1
            while t[i + k] == s[j + k]:
                k += 1
            if k > a:
                (a, b, c) = (k, j + 1, 1)
            k = 1
            while t[i + k] == s[j - k]:
                k += 1
            if k > a:
                (a, b, c) = (k, j + 1, -1)
        if not a:
            return -1
        i += a
        p.append((b, b + c * a - c))
    return [len(p)] + p

