def differentiate_polynomial(terms):
    differentiated_terms = []
    for (coeff, exp) in terms:
        if exp >= 0:
            new_coeff = coeff * exp
            new_exp = exp - 1
            if new_exp == 0:
                differentiated_terms.append(str(new_coeff))
            else:
                differentiated_terms.append(f'{new_coeff}x^{new_exp}')
    if not differentiated_terms:
        return '0'
    return ' + '.join(differentiated_terms)

