def min_modifications_for_unique_subarray_sums(N, K, A):
    ans = 1
    for i in range(K):
        d = {}
        q = i
        while q < N:
            if A[q] not in d:
                d[A[q]] = 1
            else:
                d[A[q]] += 1
            q += K
        l = list(d.values())
        s = sum(l)
        mx = max(l)
        ans += s - mx
    return ans

