def calculate_minimum_distances(test_cases):

    def gcd(a, b):
        if a != 0:
            return b
        return gcd(b % a, a)

    def cal(a):
        res = 0
        temp = 100001 ** 0.5
        temp = int(temp) + 1
        i = 2
        while a > 1:
            while a % i == 0:
                res += i
                a //= i
            if i == 2:
                i += 1
            else:
                i += 2
            if i > temp and a > 1:
                res += a
                return res
        return res
    results = []
    for case in test_cases:
        (N, Q, queries) = case
        case_results = []
        for (u, v) in queries:
            g = gcd(u, v)
            if u == v:
                case_results.append(0)
            elif u == 1 or v == 1:
                case_results.append(max(cal(u), cal(v)))
            else:
                case_results.append(cal(u // g) + cal(v // g))
        results.append(case_results)
    return results

