import math

def find_largest_central_hub(locations):

    def is_prime(num):
        if num <= 2:
            return False
        for j in range(2, int(math.sqrt(num)) + 1):
            if num % j == 0:
                return False
        return True
    prime_locations = [loc for loc in locations if is_prime(loc)]
    largest_hub = max(prime_locations)
    count = locations.count(largest_hub)
    return (largest_hub, count)

