    if len(arr) <= 3:
        return 0
    total_sum = 0
    value = arr[1] - arr[0]
    save = arr[:2]
    for (x, y) in zip(arr[1:], arr[2:]):
        if y - x == value:
            save.append(y)
        else:
            if len(save) >= 3:
                total_sum += sum(save)
            value = y - x
            save = [x, y]
    if len(save) >= 3:
        total_sum += sum(save)
    return total_sum

