def minimum_computer_strength(n, strengths, connections):
    s = [[] for _ in range(n)]
    mx_tmp = max(strengths)
    mx_tmp2 = max((g for g in strengths + [-2000000000.0] if g < mx_tmp))
    mxpt = [mx_tmp, mx_tmp2]
    mxcnt = [strengths.count(mx_tmp), strengths.count(mx_tmp2)]
    for (a, b) in connections:
        a += 1
        b -= 1
        s[a].append(b)
        s[b].append(a)
    mx = int(2000000000.0)
    for i in range(n):
        nmx = mxcnt[:]
        tmpmax = strengths[i]
        for k in s[i]:
            if strengths[k] == mxpt[0]:
                nmx[0] -= 1
            elif strengths[k] == mxpt[1]:
                nmx[1] -= 1
        if nmx[0] != mxcnt[0]:
            tmpmax = mxpt[0] + 1
        elif nmx[1] != mxcnt[1]:
            tmpmax = max(tmpmax, mxpt[1] + 1)
        if strengths[i] == mxpt[0]:
            nmx[0] -= 1
        elif strengths[i] == mxpt[1]:
            nmx[1] -= 1
        if nmx[0]:
            tmpmax = mxpt[0] + 2
        elif nmx[1]:
            tmpmax = max(mxpt[1] + 2, tmpmax)
        mx = min(mx, tmpmax)
    return mx

