def cube_root(x):
    v = max(int(x ** (1.0 / 3.0)) - 1, 0)
    while (v + 1) ** 3 < x:
        v += 1
    return v

def maximize_tower_blocks(m):
    if m <= 7:
        return (m, m)
    next_smaller = cube_root(m) ** 3
    candidate = maximize_tower_blocks(m - next_smaller)
    candidate = (candidate[0] + 1, candidate[1] + next_smaller)
    prenext_smaller = cube_root(next_smaller - 1) ** 3
    if next_smaller - prenext_smaller > m - next_smaller:
        candidate2 = maximize_tower_blocks(next_smaller - 1)
    else:
        candidate2 = candidate
    if candidate >= candidate2:
        return candidate
    else:
        return candidate2

