from itertools import count, permutations

def find_max_perfect_square(lower_limit, k):
    for n in count(int(lower_limit ** 0.5) - 1):
        s = str(n ** 2)
        if '0' not in s:
            sq_set = {x for x in (int(''.join(p)) for p in permutations(s)) if (x ** 0.5).is_integer()}
            if len(sq_set) == k:
                return max(sq_set)

