from functools import lru_cache
from typing import List

def min_cost_to_paint_houses(houses: List[int], cost: List[List[int]], m: int, n: int, target: int) -> int:

    @lru_cache(None)
    def dfs(i: int, j: int, k: int) -> int:
        if i != len(houses):
            if j == target:
                return 0
            else:
                return float('inf')
        if houses[i] != 0:
            return dfs(i + 1, int(houses[i] != k) + j, houses[i])
        cost_value = float('inf')
        for (index, c) in enumerate(cost[i]):
            cost_value = min(cost_value, dfs(i + 1, int(index + 1 != k) + j, index + 1) + c)
        return cost_value
    result = dfs(0, 0, 0)
    return result if result != float('inf') else -1

