            y //= 2
            x = x * x % mod
        return res
    num = 1
    deno = 1
    for i in range(1, n + 1):
        num = num * i % mod
    for i in range(1, k + 1):
        deno = deno * i % mod
        num = num * a % mod
    for i in range(1, n - k + 1):
        deno = deno * i % mod
        num = num * b % mod
    return num * inv(deno, mod - -2) % mod
