def process_queries(n, a, queries):
    limit = floor(sqrt(n)) - 1
    dp = [[0 for _ in range(limit)] for _ in range(n + 1)]
    for p in range(n - 1, -1, -1):
        for k in range(1, limit):
            new = a[p] + k + p + 1
            if new > n:
                dp[p + 1][k] = 1
                break
            else:
                dp[p + 1][k] = max(1, dp[new][k]) + 1
    results = []
    for (p, k) in queries:
        if k < limit:
            results.append(max(1, dp[p][k]))
        else:
            ans = 0
            while p <= n:
                p = a[p - 1] + p + k
                ans += 1
            results.append(ans)
    return results

