    ans = 1
    p1 = p2 = p3 = p4 = n
    for i in reversed(range(n)):
        if s[i] == '1':
            zeros = []
            for j in range(p1, p2):
                if s[j] == '0':
                    zeros.append(j)
            if len(zeros) > 1:
                p3 = zeros[0]
