def find_n_closest_pairs(upper_limit, k):
    square_lim = int((2 * upper_limit) ** 0.5) - 1
    squares = [n * n for n in range(1, square_lim)]
    pairs = []
    squares_set = set(squares)
    for m in range(upper_limit - 1, 1, -1):
        for b in squares:
            if b >= m:
                break
            if 2 * m - b in squares_set:
                pairs.append([m, m - b])
                if len(pairs) == k:
                    return pairs
    return pairs

