from bisect import bisect_left as bl

def find_largest_intersecting_group(n, entry_holes, exit_holes):
    I = 10000000
    n -= 1
    c = [0] * n
    for (i, x) in enumerate(entry_holes):
        c[x] = i
    d = [n - c[int(x)] for x in exit_holes]
    c = [I] * n
    for i in d:
        c[bl(c, i)] = i
    return c.index(I)

