    def pow(c):
        count = 1
        while c % 2 == 0:
            c = c // 2
            count += 1
        return count
    results = []
    for (n, a) in test_cases:
        e = 0
        o = 0
        ev = []
        for i in a:
            if i % 2 == 0:
                e += 1
                ev.append(i)
            else:
                o += 1
        if o == e:
            results.append(0)
        elif o > e:
            results.append((o - e) // 2)
        else:
            l = []
            for i in range(e):
                l.append(pow(ev[i]))
            l = sorted(l)
            d = (e - o) // 2
            b = l[:d]
            results.append(sum(b))
    return results

