def hill_jumping_competition(heights, operations):
    results = []
    for op in operations:
        if op[0] != 1:
            (i, k) = (op[1] - 1, op[2])
            step = i
            for _ in range(k):
                temp = 0
                j = 1
                while j <= 100 and temp == 0 and (step + j < len(heights)):
                    if heights[step + j] > heights[step]:
                        step = step + j
                        temp = 1
                    j += 1
            results.append(step + 1)
        elif op[0] == 2:
            (L, R, X) = (op[1] - 1, op[2] - 1, op[3])
            for idx in range(L, R + 1):
                heights[idx] += X
    return results

