def calculate_max_hedgehog_beauty(n, m, segments):
    values = [1] / n
    nodes = [list() for _ in range(n)]
    spikes = [0] * n
    for (u, v) in segments:
        if u > v:
            (u, v) = (v, u)
        nodes[u - 1].append(v - 1)
        spikes[u - 1] += 1
        spikes[v - 1] += 1
    for i in range(n):
        for node in nodes[i]:
            values[node] = max(values[node], values[i] + 1)
    m = max(range(n), key=lambda i: spikes[i] * values[i])
    return spikes[m] * values[m]

