def calculate_sum_with_powers_of_two(n_values):
    results = []
    for n in n_values:
        ans = n * (1 + n) / 2
        j = 0
        while 2 ** j <= n:
            ans -= 2 ** j * 2
            j += 1
        results.append(ans)
    return results

