def calculate_min_cost(a, k):
    n = len(a)
    if k != 1:
        return a[-1] - a[0]
    differences = [a[i] - a[i - 1] for i in range(1, n)]
    sorted_indices = sorted(range(len(differences)), key=lambda i: differences[i], reverse=True)
    for i in range(k - 1):
        differences[sorted_indices[i]] = 0
    return sum(differences)

