def can_prepare_mixture(m, n1, n2, amulus_capacities, water_capacities):
    amulus_capacities.sort(reverse=True)
    water_capacities.sort()
    wtss = set()

    def recurse(s=0, i=0):
        if i == len(water_capacities):
            wtss.add(s)
            return
        recurse(s, i + 1)
        recurse(water_capacities[i] + s, i + 1)
    recurse()
    for ws in sorted(wtss):
        t = m - ws
        s = []
        for a in amulus_capacities:
            if t >= a:
                s.append(a)
                t -= a
            if not t:
                return ('YES', sorted(s))
            if t < 0:
                break
    return ''

