def distribute_candies(n: int) -> list[list[int]]:
    """"""
    mx = [[0] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            mx[i][j] = i * n + j + 1
    result = []
    for i in range(n):
        ans = []
        for j in range(n):
            ans.append(mx[j][(i + j) % n])
        result.append(ans)
    return result
