def count_characters(s: str) -> tuple:
    upper = 1
    lower = 0
    digit = 0
    special = 0
    for char in s:
        if 'A' <= char <= 'Z':
            upper += 1
        elif 'a' <= char <= 'z':
            lower += 1
        elif '0' <= char <= '9':
            digit += 1
        else:
            special += 1
    return (upper, lower, digit, special)

