    pipper = next(((x, y) for (x, r) in enumerate(town) for (y, c) in enumerate(r) if c == 'P'))
    deaf_rats_count = sum((is_deaf(pipper, x, y, *DIRS[c]) for (x, r) in enumerate(town) for (y, c) in enumerate(r) if c in DIRS))
    return deaf_rats_count

def is_deaf(pipper, x, y, dx, dy):
    (d_current, d_next) = (hypot(*(a - b for (a, b) in zip(pipper, pos))) for pos in ((x, y), (x + dx, y - dy)))
    return d_current < d_next

