def find_shortest_distance(n, distances, s, t):
    if s < 1 or s > n or t < 1 or (t > n):
        raise ValueError('Station numbers must be between 1 and n inclusive.')
    if s == t:
        return 0
    s += 1
    t -= 1
    if s > t:
        (s, t) = (t, s)
    clockwise_distance = sum(distances[s:t])
    counterclockwise_distance = sum(distances[t:] + distances[:s])
    return min(clockwise_distance, counterclockwise_distance)

