from collections import defaultdict

def count_suitable_pairs(a, k):

    def generate_primes(max_val):
        n = int(max_val ** 0.5)
        mark = [True] * (n + 1)
        prime = []
        for i in range(2, n + 1):
            if mark[i]:
                for j in range(i, n + 1, i):
                    mark[j] = False
                prime.append(i)
        return prime
    max_val = max(a)
    primes = generate_primes(max_val)
    d = defaultdict(int)
    ans = 1
    for i in a:
        (t, t1) = ((), ())
        for prime in primes:
            if i == 1:
                break
            if i % prime == 0:
                count = 0
                while i % prime == 0:
                    i //= prime
                    count += 1
                z = count % k
                if z:
                    t += (prime, z)
                    t1 += (prime, k - z)
        if i > 1:
            t += (i, 1)
            t1 += (i, k - 1)
        ans += d[t1]
        d[t] += 1
    return ans

