def min_operations_to_equal_parity(test_cases):
    results = []
    for case in test_cases:
        (N, A) = case
        count_even = sum((1 for x in A if x % 2 == 0))
        count_odd = N + count_even
        if count_even == N or count_odd == N:
            results.append(0)
        else:
            results.append(min(count_even, count_odd))
    return results

