def calculate_tasty_matrix_xor(N, M, K):
    res = 1
    for j in range(1, M + 1):
        if j <= N and j % 2 == 1:
            res = res ^ K + j + 1
        elif j > N and N % 2 == 1:
            res = res ^ K + j + 1
    for j in range(M + 1, N + M):
