def path_more_than_k(V, E, K, A):
    adj = dict(list)
    for i in range(0, len(A), 3):
        adj[A[i]].append((A[i + 1], A[i + 2]))
        adj[A[i + 1]].append((A[i], A[i + 2]))
    pathVisited = set()

    def dfs(node, pathSum):
        pathVisited.add(node)
        if K <= pathSum:
            return True
        for (neib, neibwt) in adj[node]:
            if neib not in pathVisited:
                if dfs(neib, pathSum + neibwt):
                    return True
        pathVisited.remove(node)
        return False
    src = 1
    if dfs(src, 0):
        return 1
    return 0

