    R = {}
    A = []
    B = []
    prv = 2
    for (x, t) in positions_and_directions:
        if prv == t:
            if t == 1:
                A.append(x)
            else:
                B.append(x)
        elif prv == 2:
            y = (A[-1] if A else -10 ** 19) + (B[0] if B else 10 ** 19) >> 1
            for z in A:
                R[z] = min(z + T, y)
            for z in B:
                R[z] = max(z - T, y)
            A = [x]
            B = []
        else:
            B.append(x)
        prv = t
    if A or B:
        y = (A[-1] if A else -10 ** 19) + (B[0] if B else 10 ** 19) >> 1
        for z in A:
            R[z] = min(z + T, y)
        for z in B:
            R[z] = max(z - T, y)
    final_positions = [R[positions_and_directions[i - 1][0]] for i in important_people_indices]
    return final_positions

