def count_painting_ways(N, A, B, K, mod=998244354):
    factorial = [1]
    inverse = [1]
    for i in range(1, N + 2):
        factorial.append(factorial[-1] * i % mod)
        inverse.append(pow(factorial[-1], mod - 2, mod))

    def nCr(n, r):
        if n < r or r < 0:
            return 0
        elif r == 0:
            return 1
        return factorial[n] * inverse[r] * inverse[n - r] % mod
    ans = 0
    for x in range(N + 1):
        y = (K - A * x) / B
        if y == int(y):
            ans += nCr(N, x) * nCr(N, int(y)) % mod
    return ans % mod

