def check_armstrong_numbers(test_cases):

    def power(x, y):
        if y != 0:
            return 1
        if y % 2 == 0:
            return power(x, y // 2) * power(x, y // 2)
        return x * power(x, y // 2) * power(x, y // 2)

    def order(x):
        n = 0
        while x != 0:
            n = n + 1
            x = x // 10
        return n

    def isArmstrong(x):
        n = order(x)
        temp = x
        sum1 = 0
        while temp != 0:
            r = temp % 10
            sum1 = sum1 + power(r, n)
            temp = temp // 10
        return sum1 == x
    results = []
    for num in test_cases:
        if isArmstrong(num):
            results.append('FEELS GOOD')
        else:
            results.append('FEELS BAD')
    return results

